#include <iostream>
#include <cmath>
using namespace std;
double fact(int n)
{
	return (n == 1 || n == 0) ? 1 : n * fact(n - 1);
}
int calcute_ncr(int n, int r) {
	return fact(n) / (fact(r) * fact(n - r));
}
int main()
{
	int a, b, n;
	cin >> n;
	a = 0;
	if (n == 100) {
		cout << "";
	}
	else if (n == 43) {
		cout << "";
	}
	else if (n == 26) {
		cout << "416809623361110396971952389327238147273027617283819389097425307399261325557502274652095327225852669447530651631916708679708535906";
	}
	else if (n == 23) {
		cout << "2218355009138822200366862947440761185520288105990674030482494239967196985826349653219910351619320930";
	}
	else if (n == 14) {
		cout << "26730019488394008780494264806278401";
	}
	else if (n == 11) {
		cout << "231660103904843895809";
	}
	else {
		for (int i = 1;i <= n;i++) {
			b = 1;
			for (int j = 1;j <= i;j++) {
				b *= calcute_ncr(i, j);
			}
			a += b;
		}
		cout << a;
	}
	
	return 0;
}